const INITIAL_STATE = {
  addCategory: false,
}

const toggleHidden = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "OPEN_ADDCAT_WINDOW":
      return {
        addCategory: !action.payload,
      }

    default:
      return state
  }
}

export default toggleHidden
